mapper.select:
SELECT stockPlan.DIST_MONTH as distMonth, stockPlan.LGCS_CODE as areaCode, stockPlan.SUPP_CODE as supplierCode, stockPlan.PD_STOCK_TYPE as planType, stockPlan.HALF_CODE as halfCode, stockPlan.CLASSES_CODE as productCode, pdClass.CLASSES_NAME as productName, SUM(stockPlan.NEW_PLAN_NUM) as newPlanNum, SUM(stockPlan.ORIG_PLAN_NUM) as origPlanNum FROM ds_pd_virtual_stock_plan stockPlan INNER JOIN pd_classes pdClass ON pdClass.CLASSES_CODE = stockPlan.CLASSES_CODE AND pdClass.DEL_FLG = 0
AND pdClass.CLASSES_NAME LIKE #{filterMap.productName}
WHERE stockPlan.DIST_MONTH=#{filterMap.distMonth} AND stockPlan.LGCS_CODE=#{filterMap.areaCode} AND stockPlan.SUPP_CODE=#{filterMap.supplierCode} AND stockPlan.DEL_FLG = 0 GROUP BY stockPlan.DIST_MONTH ,stockPlan.LGCS_CODE ,stockPlan.SUPP_CODE ,stockPlan.PD_STOCK_TYPE ,stockPlan.HALF_CODE ,stockPlan.CLASSES_CODE

SELECT stockActual.DIST_MONTH as distMonth, stockActual.LGCS_CODE as areaCode, stockActual.SUPP_CODE as supplierCode, stockActual.PD_STOCK_TYPE as planType, stockActual.HALF_CODE as halfCode, stockActual.CLASSES_CODE as productCode, SUM(stockActual.NEW_ACTUAL_NUM) as newActualNum FROM ds_pd_virtual_stock_actual stockActual INNER JOIN pd_classes pdClass ON pdClass.CLASSES_CODE = stockActual.CLASSES_CODE AND pdClass.DEL_FLG = 0
AND pdClass.CLASSES_NAME LIKE #{filterMap.productName}
WHERE stockActual.DIST_MONTH=#{filterMap.distMonth} AND stockActual.LGCS_CODE=#{filterMap.areaCode} AND stockActual.SUPP_CODE=#{filterMap.supplierCode} AND stockActual.DEL_FLG = 0 GROUP BY stockActual.DIST_MONTH ,stockActual.LGCS_CODE ,stockActual.SUPP_CODE ,stockActual.PD_STOCK_TYPE ,stockActual.HALF_CODE ,stockActual.CLASSES_CODE

SELECT stockPlan.DIST_MONTH as distMonth, stockPlan.LGCS_CODE as areaCode, stockPlan.SUPP_CODE as supplierCode, stockPlan.PD_STOCK_TYPE as planType, stockPlan.CLASSES_CODE as productCode, pdClass.CLASSES_NAME as productName, SUM(stockPlan.NEW_PLAN_NUM) as sumPlanNum, SUM(stockPlan.ORIG_PLAN_NUM) as sumOrigPlanNum FROM ds_pd_virtual_stock_plan stockPlan INNER JOIN pd_classes pdClass ON pdClass.CLASSES_CODE = stockPlan.CLASSES_CODE AND pdClass.DEL_FLG = 0
AND pdClass.CLASSES_NAME LIKE #{filterMap.productName}
WHERE stockPlan.DIST_MONTH=#{filterMap.distMonth} AND stockPlan.LGCS_CODE=#{filterMap.areaCode} AND stockPlan.SUPP_CODE=#{filterMap.supplierCode} AND stockPlan.DEL_FLG = 0 GROUP BY stockPlan.DIST_MONTH ,stockPlan.LGCS_CODE ,stockPlan.SUPP_CODE ,stockPlan.PD_STOCK_TYPE ,stockPlan.CLASSES_CODE

