AND pdClass.CLASSES_NAME LIKE #{filterMap.productName}
WHERE stockActual.DIST_MONTH=#{filterMap.distMonth} AND stockActual.LGCS_CODE=#{filterMap.areaCode} AND stockActual.SUPP_CODE=#{filterMap.supplierCode} AND stockActual.DEL_FLG = 0 GROUP BY stockActual.DIST_MONTH ,stockActual.LGCS_CODE ,stockActual.SUPP_CODE ,stockActual.PD_STOCK_TYPE ,stockActual.CLASSES_CODE
