mapper.select:
select A.QLT_STD_ITEM_ID AS qltStdItemId ,A.QLT_STD_ITEM_NAME AS qltStdItemName ,B.QLT_STD_EXC_VAL AS qltStdExcVal ,B.QLT_STD_SUIT_VAL AS qltStdSuitVal ,B.QLT_STD_UNQUAL_VAL AS qltStdUnqualVal from PD_QLT_STD_ITEM AS A LEFT JOIN PD_QLT_STD AS B ON A.QLT_STD_ITEM_ID = B.QLT_STD_ITEM_ID AND
B.STANDARD_ID =#{filterMap.standardId}
where A.QLT_STD_SUB_ID=#{filterMap.qltStdSubId} AND A.QLT_STD_CLA_ID=#{filterMap.qltStdClaId}
