mapper.select:
select count(1)
from pd_technical_standard
where PD_TNC_STD_ID = #{filterMap.pdTncStdId}

SELECT
,
FROM PD_TNC_STD_ITEM B LEFT JOIN PD_TNC_STD A ON A.TNC_STD_ITEM_ID=B.TNC_STD_ITEM_ID AND A.STANDARD_ID=#{filterMap.standardId} AND A.DEL_FLG='0' WHERE LEVEL_ID=#{filterMap.levelId} AND PARENT_ID=#{filterMap.parentId}

SELECT COUNT(1)
 FROM PD_TNC_STD A
	WHERE A.STANDARD_ID=#{filterMap.standardId} AND A.TNC_STD_ITEM_ID=#{filterMap.tncStdItemId}

mapper.update:
update PD_TNC_STD
set DEL_FLG = #{delFlg}
WHERE STANDARD_ID=#{standardId}
AND TNC_STD_ITEM_ID=#{tncStdItemId}

update PD_STANDARD
set TNC_FLG = #{tncFlg}
WHERE STANDARD_ID=#{standardId}
