mapper.select:
SELECT CLASSESTREE_CODE AS classestreeCode ,A.LEVEL_NAME AS level2Name ,A.PARENT_CODE AS parentCode ,A.LEVEL_CODE AS level2Code FROM PD_CLASSESTREE A WHERE A.PARENT_CODE = #{filterMap.parentCode} AND A.DEL_FLG = 0
AND A.CLASSESTREE_CODE = #{filterMap.classestreeCode}
ORDER BY A.LEVEL_CODE

SELECT CLASSESTREE_CODE
FROM PD_CLASSESTREE
WHERE PARENT_CODE = #{filterMap.classestreeCode}
