mapper.delete:
DELETE FROM sl_pd_brand WHERE
SL_CODE=#{slCode}

    AND BRAND_EP_ID=#{brandEpId}


    AND BRAND_ID=#{brandId}

mapper.select:
SELECT SL_CODE AS slCode, BRAND_EP_ID AS brandEpId, BRAND_ID AS brandId, BRAND_NAME AS brandName, BRAND_TYPE AS brandType, CONTRACT_NO AS contractNo, TERM_BEGIN AS termBegin, TERM_END AS termEnd, VER AS ver FROM sl_pd_brand WHERE SL_CODE = #{filterMap.slCode} AND (DEL_FLG IS NULL OR DEL_FLG = 0)
AND BRAND_EP_ID = #{filterMap.brandEpId}
AND BRAND_ID = #{filterMap.brandId}
