mapper.delete:
DELETE FROM sl_ep_brand_honor WHERE
EP_ID = #{filterMap.epId}

    AND BRAND_ID=#{filterMap.brandId}


    AND HONOR_ID=#{filterMap.honorId}

mapper.select:
SELECT EP_ID AS epId, BRAND_ID AS brandId, HONOR_ID AS honorId, HONOR_DES AS honorDes, HONOR_NO AS honorNo, CERT_DATE AS certDate, CERT_ISSUER AS certIssuer, VER AS ver FROM sl_ep_brand_honor WHERE EP_ID = #{filterMap.epId}
AND BRAND_ID=#{filterMap.brandId}
AND HONOR_ID=#{filterMap.honorId}
